import java.util.Scanner;public class Question13 {    public static void main(String[] Args ){        Scanner scanner = new Scanner(System.in);        float side;        System.out.print("Enter the side: ");        side = scanner.nextFloat();        float perimeter = (float) (4 * side);        System.out.printf("The length of the side is %.2f whose perimeter is %.2f.", side, perimeter);    }}